mapper.select:
select 
FROM pm_sys_dict
WHERE del_flag = #{DEL_FLAG_NORMAL}

    and type = #{type}


    and description like concat ('%', #{description}, '%')


    and label = #{label}

order by type, sort, update_date DESC

mapper.delete:
UPDATE pm_sys_dict SET
    del_flag = #{DEL_FLAG_DELETE}
  WHERE id = #{id}
